	["Feature.AccountSecurityPrompt.Description.AuthenticatorSetupPrompt"] = [[Bảo mật của bạn rất quan trọng đối với chúng tôi: Mở Cài đặt để thiết lập Authenticator và tìm hiểu thêm trên {linkStart} trang trợ giúp {linkEnd} của chúng tôi.]],
	["Feature.AccountSecurityPrompt.Action.OpenSettings"] = [[Mở cài đặt]],
	["Feature.AccountSecurityPrompt.Description.AccountRestoresPolicyWithDate"] = [[Bắt đầu từ {date}, bạn phải bật Xác minh 2 bước qua Authenticator để đủ điều kiện khôi phục tài khoản nếu tài khoản của bạn bị tấn công.]],
	["Feature.AccountSecurityPrompt.Description.ChangeYourPasswordClickHereStyled"] = [[Để giữ an toàn cho tài khoản của bạn, vui lòng {styleTagStart} nhấp vào đây {styleTagEnd} để thay đổi mật khẩu của bạn.]],
	["Feature.AccountSecurityPrompt.Description.ChangeYourPasswordImmediatelyClickHereStyled"] = [[Để giữ an toàn cho tài khoản của bạn, vui lòng {styleTagStart} nhấp vào đây {styleTagEnd} để thay đổi mật khẩu của bạn.]],
	["Feature.AccountSecurityPrompt.Header.KeepYourAccountSafeLong"] = [[Giữ an toàn cho tài khoản của bạn bằng Xác minh 2 bước thông qua Ứng dụng xác thực]],
	["Feature.AccountSecurityPrompt.Header.KeepYourAccountSafeShort"] = [[Giữ an toàn cho tài khoản của bạn bằng Ứng dụng xác thực]],
	["Feature.AccountSecurityPrompt.Description.AuthenticatorSetupPromptNew"] = [[Chúng tôi khuyến nghị bạn bật Xác minh 2 bước qua Ứng dụng xác thực để tăng cường bảo mật tài khoản của bạn hơn. Thao tác này sẽ giúp bảo đảm sự an toàn cho tài khoản ngay cả khi mật khẩu hoặc email của bạn bị đánh cắp. Nếu tài khoản của bạn bị xâm phạm, bộ phận Hỗ trợ khách hàng sẽ đánh giá các biện pháp bảo mật đã được bạn áp dụng trong quá trình hỗ trợ khôi phục các dữ liệu quý giá bị mất của bạn. Tìm hiểu thêm tại {linkStart}trang trợ giúp{linkEnd} của chúng tôi.]],
	["Feature.AccountSecurityPrompt.Description.AuthenticatorSetupPromptNewShort"] = [[Chúng tôi khuyến nghị bạn bật Xác minh 2 bước qua Ứng dụng xác thực để giúp bảo đảm sự an toàn cho tài khoản của bạn. Nếu tài khoản của bạn bị xâm phạm, bộ phận Hỗ trợ khách hàng sẽ đánh giá các biện pháp bảo mật đã được bạn áp dụng trong quá trình hỗ trợ khôi phục các dữ liệu quý giá bị mất của bạn. Tìm hiểu thêm tại {linkStart}trang trợ giúp{linkEnd} của chúng tôi.]],
	["Feature.AccountSecurityPrompt.Header.AccountRestorePolicyMessageSubject"] = [[Bật Xác minh 2 bước thông qua Ứng dụng xác thực để giữ an toàn cho tài khoản của bạn]],
	["Feature.AccountSecurityPrompt.Description.AccountRestorePolicyMessageBody"] = [[Xin chào, {lineBreak}{lineBreak}Chúng tôi khuyến nghị bạn bật Xác minh 2 bước qua Ứng dụng xác thực để bảo mật tài khoản của bạn hơn. Thao tác này sẽ giúp bảo đảm sự an toàn cho tài khoản của bạn ngay cả khi mật khẩu hoặc email của bạn bị đánh cắp. Trong trường hợp tài khoản của bạn bị xâm phạm, bộ phận Hỗ trợ khách hàng sẽ đánh giá các biện pháp bảo mật đã được bạn áp dụng trong quá trình hỗ trợ khôi phục các dữ liệu quý giá bị mất của bạn. {lineBreak}{lineBreak} Bạn có thể thiết lập ứng dụng xác thực trên tài khoản của mình bằng cách truy cập vào trang {securityPageLinkStart}https://www.roblox.com/my/account#!/security{linkEnd}. {lineBreak}{lineBreak}Tìm hiểu thêm tại {helpPageLinkStart}trang trợ giúp{linkEnd} của chúng tôi.]],
	["Feature.AccountSecurityPrompt.Header.TwoStepVerificationUpdate"] = [[Cập nhật Xác minh 2 bước]],
	["Feature.AccountSecurityPrompt.Description.AuthenticatorForcedOverEmail"] = [[Bạn vừa bật Xác minh 2 bước qua cả Email lẫn Ứng dụng xác thực trong thời gian gần đây. Trong hầu hết các trường hợp, để giữ an toàn cho tài khoản của bạn, bạn sẽ chỉ được tiến hành xác minh qua Ứng dụng xác thực.]],
	["Feature.AccountSecurityPrompt.Description.AuthenticatorForcedOverEmailPresent"] = [[Hiện tại bạn đã bật Xác minh 2 bước qua cả Email lẫn Ứng dụng xác thực. Trong hầu hết các trường hợp, để giữ an toàn cho tài khoản của bạn, bạn sẽ chỉ được tiến hành xác minh qua Ứng dụng xác thực.]],
	["Feature.AccountSecurityPrompt.Description.LearnMoreHere"] = [[Tìm hiểu thêm tại {linkStart}đây{linkEnd}.]],
	["Feature.AccountSecurityPrompt.Header.BoostYourAccountSecurity"] = [[Tăng cường bảo mật cho tài khoản của bạn]],
	["Feature.AccountSecurityPrompt.Description.ReceiveSecurityCodesMessage"] = [[Nhận mã bảo mật trên một thiết bị khác để bảo vệ trải nghiệm, robux, vật phẩm giới hạn, nhóm của bạn, và nhiều dữ liệu khác.]],
	["Feature.AccountSecurityPrompt.Action.SetUpAuthenticatorInBanner"] = [[Thiết lập Ứng dụng xác thực]],
	["Feature.AccountSecurityPrompt.Header.AccountRestoresPolicyUpdateV3"] = [[Bật Xác minh 2 bước thông qua Ứng dụng xác thực]],
	["Feature.AccountSecurityPrompt.Description.AccountRestoresPolicyUpdateV3"] = [[Bạn có thể thực hiện thao tác này bằng cách đi đến {linkStart}Cài đặt tài khoản{linkEnd}. Nếu tài khoản của bạn bị xâm phạm, Roblox không chắc có thể khôi phục tài khoản của bạn hoặc khôi phục các dữ liệu có giá trị của tài khoản, bao gồm các vật phẩm và tiền ảo bị mất.]],
	["Feature.AccountSecurityPrompt.Description.AccountRestoresPolicyUpdateV3Short"] = [[Nếu tài khoản của bạn bị xâm phạm, Roblox không chắc có thể khôi phục tài khoản của bạn hoặc khôi phục các dữ liệu có giá trị của tài khoản, bao gồm các vật phẩm và tiền ảo bị mất.]],
	["Feature.AccountSecurityPrompt.Description.AccountRestoresPolicyUpdateV3MessageBody"] = [[Vui lòng bật Xác minh 2 bước thông qua Ứng dụng xác thực để bảo vệ tài khoản của bạn. Bạn có thể thực hiện thao tác này bằng cách đi đến {linkStartSettings}Cài đặt tài khoản{linkEndSettings}. {lineBreak}{lineBreak}Nếu tài khoản của bạn bị xâm phạm, Roblox không chắc có thể khôi phục tài khoản của bạn hoặc khôi phục các dữ liệu có giá trị của tài khoản, bao gồm các vật phẩm và tiền ảo bị mất. Tìm hiểu thêm trong {linkStartHelp}Trang trợ giúp{linkEndHelp} của chúng tôi.]],
	["Feature.AccountSecurityPrompt.Header.AccountRestoresPolicyUpsell"] = [[Hãy bảo vệ tài khoản của bạn!]],
	["Feature.AccountSecurityPrompt.Description.AccountRestoresPolicyUpsellMessageBody"] = [[Vui lòng bật Xác minh 2 bước thông qua Ứng dụng xác thực để bảo vệ tài khoản của bạn. Bạn có thể thực hiện thao tác này bằng cách đi đến {linkStartSettings}Cài đặt tài khoản{linkEndSettings}. Nếu tài khoản của bạn bị xâm phạm, Roblox không thể đảm bảo sẽ có thể khôi phục tài khoản của bạn hoặc khôi phục các dữ liệu có giá trị của tài khoản, bao gồm các vật phẩm và tiền ảo bị mất.]],
